import React, { useState } from "react";
import PropTypes from "prop-types";
import { Button, Flex, styled } from "reakit";
import TrashIcon from "../assets/trash-icon.png";
import Popup from "../Popup";
import PopupTrash from "../PopupTrash";

const Container = styled.div`
  margin: 10px;
  padding: 10px;
  border: 1px solid lightgrey;
  border-radius: 2px;
  cursor: pointer;
  background-color: #6a696930;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const FlexStyled = styled(Flex)`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const LogoIcon = styled.img`
  height: 20px;
  width: 20px;
  padding: 12px 0 0 30px;
  cursor: pointer;
  &:hover {
    opacity: 0.8;
  }
`;

const Quote = (props) => {
  const { num, quote } = props;

  const [isOpen, setIsOpen] = useState(false);
  const [isOpenTrashPopup, setIsOpenTrashPopup] = useState(false);

  return (
    <Container>
      <Title>{"Kategorija 1"}</Title>

      <QuoteList>
        {data.map((data, index) => (
          <>
            {/* //here must go quote=... because it is defined in Quote component */}
            <Quote num={index} quote={data.title} />
          </>
        ))}
      </QuoteList>
      <ButtonWrapper>
        <StyledButton onClick={() => setIsOpen(true)}>Add quote</StyledButton>
      </ButtonWrapper>
      <Popup
        content={""}
        open={isOpen}
        onClose={setIsOpen}
        screen={"add_quote"}
      ></Popup>
    </Container>
  );
};

//validate props in Quote where was  <Quote/> called
Quote.propTypes = {
  quote: PropTypes.string.isRequired,
};

export default Quote;
