const isLogged = (state = false, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      let acc = action.data;
      localStorage.setItem(
        "user",
        JSON.stringify({
          acc,
        })
      );

      return action.data;

    case "USER_LOGOUT":
      return "";

    case "SET_NEW_USERNAME":
      const newAcc = action.newUser.usernameProfile;
      console.log("fff", newAcc);
      localStorage.setItem(
        "user",
        JSON.stringify({
          newAcc,
        })
      );
      return action.newUser.usernameProfile;
    default:
      return state;
  }
};
export default isLogged;
