const isLogged = (state = false, action) => {
  switch (action.type) {
    case "USER_LOGIN":
      const acc = action.data;
      localStorage.setItem(
        "user",
        JSON.stringify({
          acc,
        })
      );

      return action.data;

    case "USER_LOGOUT":
      return "";

    case "SET_NEW_USERNAME":
      const newState = action.data;
      var newItem = {
        id: state.posts.length + 1,
        title: action.data,
        body: "dfada",
      };
      newState.push(newItem);

      localStorage.setItem(
        "cart",
        JSON.stringify({
          newState,
        })
      );

      return action.data;
    default:
      return state;
  }
};
export default isLogged;
