import React from "react";
import { styled } from "reakit";
import { Row, Column } from "react-rasta";

import { Colors, Size, media } from "common/Styles";

const ToggleWrapper = styled.label`
  position: relative;
  display: inline-block;
  width: 60px;
  height: 34px;
  margin-top: 15px;
  margin-bottom: 0;
  input {
    display: none;
  }
`;

const ToggleElement = styled.div`
  position: relative;
  cursor: pointer;
  border-radius: 34px;
  height: 100%;
  width: 100%;
  background-color: #ccc;
  -webkit-transition: 0.4s;
  transition: 0.4s;
  border: 1px solid ${Colors.OutlineGray};

  :before {
    content: "";
    position: absolute;
    border-radius: 50%;
    height: 26px;
    width: 26px;
    left: 4px;
    bottom: 4px;
    background-color: white;
    -webkit-transition: 0.25s;
    transition: 0.25s;
  }
`;

const StyledInput = styled.input`
  :checked + ${ToggleElement} {
    background-color: white;
  }

  :focus + ${ToggleElement} {
    box-shadow: 0 0 1px #f32195;
  }

  :checked + ${ToggleElement}:before {
    -webkit-transform: translateX(26px);
    -ms-transform: translateX(26px);
    transform: translateX(26px);
    background-color: ${Colors.Astral};
  }
`;

const StyledNote = styled.h6`
  color: ${Colors.DustyGray};
  background: ${Colors.white};
  text-align: start;
  font-size: 1rem;
  // padding-top: ${Size.headingSpacingHorizontal};
`;

const Wrapper = styled.div`
  padding-left: 15px;
  padding-right: 15px;
  ${media.lessThan(Size.contentMaxWidth.md)`
    padding-left: 0;
    padding-right: 0;
  `};
`;

const MenuButton = ({ label, checked, onToggleChange }) => {
  return (
    <Row>
      <Column>
        <Wrapper>
          <StyledNote>{label}</StyledNote>
          <ToggleWrapper>
            <StyledInput
              id="toggle"
              type="checkbox"
              checked={checked}
              onChange={(e) => onToggleChange(e.target.checked)}
            />
            <ToggleElement />
          </ToggleWrapper>
        </Wrapper>
      </Column>
    </Row>
  );
};

export default MenuButton;
