import { IS_LOGGED } from "../types";

const initState = {
  posts: [
    { id: "1", title: "Prvi", body: "ovo je test" },
    { id: "2", title: "Drugi", body: "test je ovo" },
    { id: "3", title: "Treci", body: "test " },
    { id: "4", title: "Cetvrti", body: "tovo" },
    { id: "5", title: "Peti", body: " je ovo" },
  ],
};

const data = (state = initState, action) => {
  localStorage.setItem(
    "cart",
    JSON.stringify({
      state,
    })
  );

  switch (action.type) {
    case "SET_QUOTE": {
      var newItem = {
        "product-name": itemContainer.find("h2.product-name a").text(),
        "product-image": itemContainer
          .find("div.product-image img")
          .attr("src"),
        "product-price": itemContainer.find("span.product-price").text(),
      };

      oldItems.push(newItem);
    }
  }
  return state;
};

export default data;
