const isLogged = (state = false, action) => {
  let acc = null;

  switch (action.type) {
    case "USER_LOGIN":
      acc = {
        username: action.data.username,
        password: action.data.password,
      };

      localStorage.setItem(
        "user",
        JSON.stringify({
          acc,
        })
      );

      return action.data;

    case "USER_LOGOUT":
      return "";

    case "SET_NEW_USERNAME":
      const newAcc = action.newUser.usernameProfile;
      acc["username"] = newAcc;

      localStorage.setItem(
        "user",
        JSON.stringify({
          acc,
        })
      );
      return action.newUser.usernameProfile;
    default:
      return state;
  }
};
export default isLogged;
