const isLogged = (state = {}, action) => {
  let acc = null;

  switch (action.type) {
    case "USER_LOGIN":
      return action.data;

    case "USER_LOGOUT":
      return state;

    case "SET_NEW_USERNAME":
      const newUser = action.newUser.usernameProfile;
      return { ...state, username: newUser };
    default:
      return state;
  }
};
export default isLogged;
