import React from "react";
import * as style from "../../../assets/common/Styles";
import { Button, Input, styled } from "reakit";
import { Field, reduxForm } from "redux-form";
import { connect } from "react-redux";

const Wrapper = styled.form`
  display: flex;
  justify-content: center;
  flex-direction: column;
  margin: 100px;
  gap: 20px;
`;

const Label = styled.label`
  background-color: #cbb6b6;
  color: ${style.Colors.white};
  font-size: ${style.Fonts.FontSize.medium};
  padding: 30px;
`;

const StyledButton = styled(Button)`
  background-color: #7c7c7c;
  border: none;
  color: ${style.Colors.white};
  cursor: pointer;
  font-size: ${style.Fonts.FontSize.medium};
  font-weight: bold;
  margin: 1em 0;
  padding: 1em 2em;
  border-radius: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  &:hover {
    opacity: 0.8;
  }
`;
const FlexStyled = styled(Flex)`
  display: flex;
  justify-content: space-between;
  position: relative;
  width: 70%;
  margin: 0 auto;
  height: auto;
  max-height: 70vh;
  margin-top: calc(100vh - 85vh - 20px);
  background: #fff;
  border-radius: 4px;
  padding: 20px;
  border: 1px solid #999;
  overflow: auto;
`;

const EditProfile = (props) => {
  const { user, handleSubmit } = props;

  return (
    <Wrapper>
      <FlexStyled>
        <Field
          key={"usernameProfile"}
          name="usernameProfile"
          component={Input}
          type="input"
          label={"Username"}
        />
        <StyledButton type="submit" id="done">
          Done
        </StyledButton>
      </FlexStyled>
    </Wrapper>
  );
};

const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};

export default connect(mapStateToProps)(
  reduxForm({
    // a unique name for the form
    form: "profilEditForm",
  })(EditProfile)
);
