import React from "react";
import PropTypes from "prop-types";
import Loadable from "react-loadable";
import { Box, Flex, styled } from "reakit";
import { Colors, Fonts } from "../../assets/common/Styles";
import * as style from "../../assets/common/Styles";
import CloseIcon from "../assets/icons/close-icon.png";

const NotificationHolder = styled(Flex)`
  width: 100%;
  padding: 0 30px;
`;

const NotificationWrapper = styled(Flex)`
  padding: 0.6rem;
  margin: 0 auto;
  margin-top: 0.5rem;
  margin-bottom: 0.5rem;
  display: ${(props) => (props.show ? "flex" : "none")};
  background-color: ${style.Colors.Astral};
  color: ${style.Colors.white};
  border-radius: 0.1rem;
  align-items: center;
  max-width: 1440px;
  width: 100%;
`;

const Notification = (props) => {
  const { show, onClose, message } = props;
  return (
    <NotificationHolder>
      <NotificationWrapper backgroundColor={style.Colors.ErrorRed} show={show}>
        {message}

        <LogoIcon src={CloseIcon} onClick={() => onClose(false)} />
      </NotificationWrapper>
    </NotificationHolder>
  );
};

export default Notification;
